import React, { Component } from "react";
import Masonry from 'react-masonry-component';

import Todo from "../components/Todo";

class TodoList extends Component {

 state = {
   low: true,
   medium: true,
   height: true
 };

 handleChecked = event => {
  const { name, checked } = event.target;

  this.setState({
    [name]:checked
  });
};


  render() {
    const todos = this.props.todos;
    const {low, medium, height} = this.state;

    let finalTodos = [];
        if (low === true) {
          const filteredCheck = todos.filter(todo => {
          return todo.importance === "low";
          });
        finalTodos = finalTodos.concat(filteredCheck);
        }

        if (medium === true) {
          const filteredCheck = todos.filter(todo => {
            return todo.importance === "medium";
          });
          finalTodos = finalTodos.concat(filteredCheck);
        }

        if (height === true) {
          const filteredCheck = todos.filter(todo => {
            return todo.importance === "height";
          });
          finalTodos = finalTodos.concat(filteredCheck);
        }

        const { search } = this.state;
        if (search !== "") {
          finalTodos = finalTodos.filter(todo => {
            return todo.title.includes(search) || todo.text.includes(search);
          });
        }



    return (
    <>
      <div class="custom-control custom-checkbox custom-control-inline">
        <input 
          type="checkbox" 
          className="custom-control-input" 
          id="defaultInline1"
          name = "low"
          checked= {low}
          onChange={this.handleChecked}
        />
        <label class="custom-control-label" for="defaultInline1" >
          <span className="importanceIcon dotLow"></span> 
          <span> </span>
          low
        </label>
      </div>

      <div class="custom-control custom-checkbox custom-control-inline">
        <input 
          type="checkbox" 
          className="custom-control-input" 
          id="defaultInline2"
          name = "medium"
          checked= {medium}
          onChange={this.handleChecked}
        />
        <label class="custom-control-label" for="defaultInline2">
        <span className="importanceIcon dotMedium"></span> 
        <span> </span>
        medium</label>
      </div>

      <div class="custom-control custom-checkbox custom-control-inline">
        <input 
          type="checkbox" 
          className="custom-control-input" 
          id="defaultInline3"
          name = "height"
          checked= {height}
          onChange={this.handleChecked}
        />
        <label class="custom-control-label" for="defaultInline3">
        <span className="importanceIcon dotHeight"></span> 
        <span> </span>
        height</label>
        <br/>
        <br/>
      </div>

      <Masonry className="todos">
        {todos.map((todoData) => {

          const handleStartTodo = () => {
            todoData.state = 'inProgress';
            this.props.onEdit(todoData);
          }
          const handleFinishTodo = () => {
            todoData.state = 'finished';
            this.props.onEdit(todoData);
          };

          const handleRemoveTodo = () => {
            this.props.onRemove(todoData);
          };

          return (
            <Todo
              todo={todoData}
              key={todoData.id}
              onStart={handleStartTodo}
              onFinish={handleFinishTodo}
              onRemove={handleRemoveTodo}
            />
          );
        })}
      </Masonry>
      </>

    );
  }
}

export default TodoList;
