import React, { Component } from "react";
import Masonry from 'react-masonry-component';

import Todo from "../components/Todo";

class TodoList extends Component {
  render() {
    const todos = this.props.todos;

    return (
    <>

      <div class="custom-control custom-checkbox custom-control-inline">
        <input 
          type="checkbox" 
          className="custom-control-input" 
          id="defaultInline1"
          name = "importance"
          checked={this.props.importance}
          onChange={this.handleChange}
        />
        <label class="custom-control-label" for="defaultInline1">{this.props.importance}</label>
      </div>


      <div class="custom-control custom-checkbox custom-control-inline">
        <input 
          type="checkbox" 
          className="custom-control-input" 
          id="defaultInline1"
          name = "importance"
          checked={this.props.importance}
          onChange={this.handleChange}
        />
        <label class="custom-control-label" for="defaultInline1">{this.props.importance}</label>
      </div>


      <div class="custom-control custom-checkbox custom-control-inline">
        <input 
          type="checkbox" 
          className="custom-control-input" 
          id="defaultInline1"
          name = "importance"
          checked={this.props.importance}
          onChange={this.handleChange}
        />
        <label class="custom-control-label" for="defaultInline1">{this.props.importance}</label>
      </div>
      <br/>

      <Masonry className="todos">
        {todos.map((todoData) => {

          const handleStartTodo = () => {
            todoData.state = 'inProgress';
            this.props.onEdit(todoData);
          }
          const handleFinishTodo = () => {
            todoData.state = 'finished';
            this.props.onEdit(todoData);
          };

          const handleRemoveTodo = () => {
            this.props.onRemove(todoData);
          };

          return (
            <Todo
              todo={todoData}
              key={todoData.id}
              onStart={handleStartTodo}
              onFinish={handleFinishTodo}
              onRemove={handleRemoveTodo}
            />
          );
        })}
      </Masonry>
      </>

    );
  }
}

export default TodoList;
