import React, { Component } from "react";

import axios from "../axios";

import TodoButtons from "./TodoButtons";
import moment from "moment";

class Todo extends Component {
  renderText = () => {
    const { text } = this.props.todo;
    if (!text) return null;

    return (
      <div className="card-text" dangerouslySetInnerHTML={{ __html: text }} />
    );
  };

  handleStart = async () => {
    await axios.patch("/todos/" + this.props.todo.id), {
      todoState: 'notStarted'
    }
    this.props.getState();
  }

  handleInProgress = async () => {
    await axios.patch("/todos/" + this.props.todo.id), {
      todoState: 'inProgress'
    }
    this.props.getState();
  }

  handleFinish = async () => {
    await axios.patch("/todos/" + this.props.todo.id, {
      todoState: 'finished'
    });
    this.props.getState();
  };

  handleRemove = async () => {
    await axios.delete("/todos/" + this.props.todo.id);
    this.props.onRemove();
  };

  render() {
    const { createdAt, title, todoState } = this.props.todo;
    let classes = "card";
    if (todoState = 'finished') {classes += " border-success"} else {classes = " border-non-success" };

    return (
      <div className="todo mb-2">
        <div className={classes}>
          <div className="card-body">
            <h5 className="card-title">{title}</h5>
            <h6 className="card-subtitle text-muted mb-2">
              Pridan√©: {moment(createdAt).format("DD MMM.'YY")}
            </h6>
            {this.renderText()}
            <TodoButtons
              todo={this.props.todo}
              onStart = {this.handleStart}
              onFinish={this.handleFinish}
              onRemove={this.handleRemove}
            />
          </div>
        </div>
      </div>
    );
  }
}

export default Todo;
