import React, { Component } from 'react';

class TodoButtons extends Component {
  render () {
    const {state} = this.props.todo;
    let finishButton;
    let startButton;

    if (state === 'notStarted') {
      startButton = (
        <button type="button" className="btn btn-warning float-left" onClick={this.props.onStart}>
          Za훾a큐
        </button>
      ) 
    }

    if (state !== 'finished') {
      finishButton = (
        <button type="button" className="btn btn-success float-right" onClick={this.props.onFinish}>
          Dokon훾i큐
        </button> 
      )
    } 

    return (
      <>
        <button class="closeButton" type="button" class="close" aria-label="Close" onClick={this.props.onRemove}>
          <span aria-hidden="true">&times;</span>
        </button>
        {startButton}
        {finishButton}
      </>
    )
  }
}

export default TodoButtons;
