import React, { Component } from "react";
import Masonry from 'react-masonry-component';

import Todo from "../components/Todo";

class TodoList extends Component {
  render() {
    const todos = this.props.todos;

    return (
    <>

      <div class="custom-control custom-checkbox custom-control-inline">
        <input type="checkbox" class="custom-control-input" id="defaultInline1"/>
        <label class="custom-control-label" for="defaultInline1">{renderImportance()}</label>
      </div>


      <div class="custom-control custom-checkbox custom-control-inline">
        <input type="checkbox" class="custom-control-input" id="defaultInline2"/>
        <label class="custom-control-label" for="defaultInline2">2</label>
      </div>


      <div class="custom-control custom-checkbox custom-control-inline">
        <input type="checkbox" class="custom-control-input" id="defaultInline3"/>
        <label class="custom-control-label" for="defaultInline3">3</label>
      </div>
      <br/>

      <Masonry className="todos">
        {todos.map((todoData) => {

          const handleStartTodo = () => {
            todoData.state = 'inProgress';
            this.props.onEdit(todoData);
          }
          const handleFinishTodo = () => {
            todoData.state = 'finished';
            this.props.onEdit(todoData);
          };

          const handleRemoveTodo = () => {
            this.props.onRemove(todoData);
          };

          return (
            <Todo
              todo={todoData}
              key={todoData.id}
              onStart={handleStartTodo}
              onFinish={handleFinishTodo}
              onRemove={handleRemoveTodo}
            />
          );
        })}
      </Masonry>
      </>

    );
  }
}

export default TodoList;
